import React, { FC, useState, useCallback } from "react"
import { Route, Switch } from "react-router-dom"

import styles from "./App.module.css"

import { Nav } from "./components/Nav/Nav"
import { Content } from "./components/Content/Content"
import { Sidebar } from "./components/Sidebar/Sidebar"
import { Bookmarks } from "./components/Bookmarks/Bookmarks"

export type postProps = {
  value: string
  id: number
  isFlagLike: boolean
}

export const App: FC = React.memo(() => {
  const [postNews, SetPostNews] = useState<postProps[]>([])

  const toggle = useCallback(
    (id: number) => {
      // SetPostNews(
      //   postNews.map((post) => {
      //     if (post.id === id) {
      //       post.isFlagLike = !post.isFlagLike
      //     }
      return id
      //   })
      // )
    },
    [postNews]
  )

  return (
    <>
      <main className={styles.main}>
        <Nav SetPostNews={SetPostNews} />

        <section className={styles.list}>
          <Switch>
            <Route
              component={() => <Content toggle={toggle} postNews={postNews} />}
              path="/"
              exact
            />
            <Route
              component={() => <Bookmarks postNews={postNews} toggle={toggle} />}
              path="/Bookmarks"
            />
            <Route
              component={() => <Content toggle={toggle} postNews={postNews} />}
              path="/Profile"
            />
          </Switch>
        </section>

        <Sidebar />
      </main>
    </>
  )
})
