import React, { FC, useState, useCallback } from 'react'
import { BrowserRouter as Router, Link, useParams, useRouteMatch } from 'react-router-dom'
import styles from './profileNav.module.css'

const NavItems = ['Tweets', 'Tweets and replies', 'Media', 'Likes'] //вопросики

export const ProfileNav: FC = () => {
  let params = useParams()
  let match = useRouteMatch('/blog/:slug')
  console.log('match', match)
  console.log('params', params)

  const [itemActive, setItemActive] = useState('Tweets')

  const onClickHandler = useCallback((e: React.MouseEvent) => {
    const title: string = e.currentTarget.innerHTML
    setItemActive(title)
  }, [])
  return (
    <nav className={styles.wrp}>
      <ul className={styles.profileNav}>
        {NavItems.map((item, index) => (
          <li
            key={index}
            onClick={onClickHandler}
            className={item === itemActive ? styles.profileNavActive : styles.profileItems}
          >
            <Router>
              <Link to={`//localhost:3000/${item}`}>{item}</Link>
            </Router>
          </li>
        ))}
      </ul>
    </nav>
  )
}
