import React, { FC, useCallback, useState } from "react"
import { BrowserRouter as Router } from "react-router-dom"
import { Profile } from "../Profile/Profile"
import { ProfileNav } from "../ProfileNav/ProfileNav"
import { ProfileContent } from "./ProfileContent"
import { Button } from "../Nav/components/button/Button"

// import { postProps } from "../../App"

export type postProps = {
  value: string
  id: number
  isFlagLike: boolean
}

export const Content: FC<postProps> = React.memo(() => {
  const [postNews, SetPostNews] = useState<postProps[]>([])

  const toggle = useCallback((id: number) => {
    SetPostNews((prev) =>
      prev.map((post) => {
        if (post.id === id) {
          post.isFlagLike = !post.isFlagLike
        }
        debugger
        return post
      })
    )
  }, [])

  return (
    <>
      <Profile />
      <Button SetPostNews={SetPostNews} />
      <Router>
        <ProfileNav />
        <ProfileContent toggle={toggle} postNews={postNews} />
      </Router>
    </>
  )
})
