import React, { FC, useState, useCallback, useEffect } from "react"
import { Route, Switch } from "react-router-dom"
import { Profile } from "./Profile/Profile"
import { Bookmarks } from "./Bookmarks/Bookmarks"

export type postProps = {
  value: string
  id: number
  isFlagLike: boolean
}

export const Content: FC = React.memo(() => {
  const [postNews, SetPostNews] = useState<postProps[]>([
    { value: "test", id: 1, isFlagLike: false },
    { value: "test2", id: 2, isFlagLike: false },
    { value: "test2", id: 3, isFlagLike: false },
    { value: "test2", id: 4, isFlagLike: false },
    { value: "test2", id: 5, isFlagLike: false },
    { value: "test2", id: 6, isFlagLike: false },
    { value: "test2", id: 7, isFlagLike: false },
    { value: "test2", id: 8, isFlagLike: false },
    { value: "test2", id: 9, isFlagLike: false }
  ])

  // useEffect(() => {
  //   const saved = JSON.parse(localStorage.getItem("post") || "[]") as postProps[]
  //   SetPostNews(saved)
  // }, [])

  // useEffect(() => {
  //   localStorage.setItem("post", JSON.stringify(postNews))
  // }, [postNews])

  const addPost = useCallback((value: string) => {
    const newPost: postProps = {
      value: value,
      isFlagLike: false,
      id: Date.now()
    }

    SetPostNews((prev) => [newPost, ...prev])
  }, [])

  console.log(postNews)

  return (
    <Switch>
      <Switch>
        <Route
          path="/"
          component={() => <Profile postNews={postNews} SetPostNews={SetPostNews} />}
          exact
        />
        <Route
          path="/Profile"
          component={() => <Profile postNews={postNews} SetPostNews={SetPostNews} />}
          exact
        />
        <Route
          path="/Bookmarks"
          component={() => <Bookmarks postNews={postNews} SetPostNews={SetPostNews} />}
          exact
        />
      </Switch>
    </Switch>
  )
})
