import React, { useState, useCallback } from 'react'
import { BrowserRouter as Router, Link } from 'react-router-dom'

import Button from './Button'
import logo from '../../images/svg/logo.svg'
import styles from './nav.module.css'
import dataNav from './data-nav'

const Nav: React.FC = (props) => {
  const [activeElement, setActiveElement] = useState('Profile')

  // const handleClick: any = (e: React.MouseEvent) => {
  //   const title: string = e.currentTarget.innerHTML

  //   setActiveElement(title)
  // }

  const handleClick: any = useCallback((e: React.MouseEvent) => {
    const title: string = e.currentTarget.innerHTML
    setActiveElement(title)
  }, [])

  return (
    <Router>
      <nav className={styles.menu}>
        <ul>
          <li>
            <Link to={'/'}>{<img src={logo} alt="logo" className={styles.menu__logo} />}</Link>
          </li>
          {dataNav.map((el) => (
            <li key={el.title} className={styles.menu__item}>
              <img
                src={el.icon}
                className={el.title === activeElement ? styles.icon__active : styles.icon__item}
                alt="icon"
              />
              <Link
                to={`//localhost:3000/${el.title}`}
                onClick={handleClick}
                className={el.title === activeElement ? styles.title__active : styles.title__item}
                data-testid="link__nav"
              >
                {el.title}
              </Link>
            </li>
          ))}
        </ul>
        <Button />
      </nav>
    </Router>
  )
}

export default Nav
