import React, { FC, useState } from 'react'
import { Router, Route } from 'react-router-dom'

import styles from './App.module.css'

import { Nav } from './components/Nav/Nav'
import {Content} from './components/Content/Content'
import { Sidebar } from './components/Sidebar/Sidebar'
import {Bookmarks} from './components/Bookmarks/Bookmarks'


import { createBrowserHistory } from 'history'
const browserHistory = createBrowserHistory()

type postProps = {
  value: string,
  id: number,
  isFlagLike: boolean
}

export const App: FC = () => {
  const [postNews, SetPostNews] = useState<postProps[]>([])

  const toggle = (id: number) => {
    SetPostNews(postNews.map((post) => {
        if (post.id === id) {
          post.isFlagLike = !post.isFlagLike
        }
        return post
      })
    )
  }

  console.log(postNews)


  return (
    <>
      {/* <Router history={browserHistory}> */}
        <main className={styles.main}>
          <Nav SetPostNews={SetPostNews} />
          <section className={styles.list}>
            {/* <Content toggle={toggle} postNews={postNews}/> */}
            <Route path="/Bookmarks" component={() => <Bookmarks />} />
            <Route path="/Profile" component={() => <Content postNews={postNews} toggle={toggle} />} />
          </section>
          <Sidebar />
        </main>
      {/* </Router> */}
    </>
  )
}
