import React, { FC, useState, useCallback, useEffect } from "react"
import { BrowserRouter, Route, Switch } from "react-router-dom"
import { Nav } from "../components/Nav/Nav"
import { Content } from "../components/Content/Content"

import { Bookmarks } from "../components/Bookmarks/Bookmarks"

import styles from "../App.module.css"

export type postProps = {
  value: string
  id: number
  isFlagLike: boolean
}

export const Test: FC = () => {
  const [postNews, SetPostNews] = useState<postProps[]>([])

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("post") || "[]") as postProps[]
    SetPostNews(saved)
  }, [])

  useEffect(() => {
    localStorage.setItem("post", JSON.stringify(postNews))
  }, [postNews])

  const addPost = useCallback((value: string) => {
    const newPost: postProps = {
      value: value,
      isFlagLike: false,
      id: Date.now()
    }

    SetPostNews((prev) => [newPost, ...prev])
  }, [])

  const toggle = useCallback((id: number) => {
    SetPostNews((prev) =>
      prev.map((post) => {
        return { ...post, isFlagLike: post.id === id ? !post.isFlagLike : post.isFlagLike }
      })
    )
  }, [])

  return (
    <>
      <Nav addPost={addPost} />
      <section className={styles.list}>
        <Switch>
          <Route component={() => <Content toggle={toggle} postNews={postNews} />} path="/" exact />
          <Route
            component={() => <Content toggle={toggle} postNews={postNews} />}
            path="/Profile"
          />
          <Route
            component={() => <Bookmarks postNews={postNews} toggle={toggle} />}
            path="/Bookmarks"
          />
        </Switch>
      </section>
    </>
  )
}
