import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Link } from 'react-router-dom'

import logo from '../../images/svg/logo.svg'
import styles from './nav.module.css'
import dataNav from './data-nav'

const Nav: React.FC = (props) => {
  const [flag, setFlag] = useState(false)

  const handleClick: any = (e: React.MouseEvent) => {
    e.preventDefault()
    const title: string = e.currentTarget.innerHTML
    const a: any = dataNav.find((el) =>
      el.isFlag === true ? (el.isFlag = false) : (el.isFlag = true)
    )
    console.log(`Click ${title}`, a)
    return a
  }

  return (
    <Router>
      <nav className={styles.menu}>
        <ul>
          <li>
            <Link to={'/'}>{<img src={logo} alt="logo" className={styles.menu__logo} />}</Link>
          </li>
          {dataNav.map((el) => (
            <li key={el.title} className={styles.menu__item}>
              <img
                src={el.icon}
                className={el.isFlag ? styles.icon__active : styles.icon__item}
                alt="icon"
              />
              <Link
                to={`//localhost:3000/${el.title}`}
                onClick={handleClick}
                className={el.isFlag === true ? styles.title__active : styles.title__item}
                data-testid="link__nav"
              >
                {el.title}
              </Link>
            </li>
          ))}
        </ul>
        <button type="submit" className={styles.button__tweet} data-testid="toggle">
          Tweet
        </button>
      </nav>
    </Router>
  )
}

export default Nav
