import React, { FC, Suspense, lazy, useState } from 'react'
import styles from './button.module.css'
import { Link, Route } from 'react-router-dom'

const PopupTweet = lazy(() => import('../../../PopupTweet/PopupTweet'))

type props = {
  SetPostNews: any
}

export const Button: FC<props> = React.memo(({ SetPostNews }) => {
  const [buttonLink, setButtonLink] = useState(true)

  const onClick = () => {
    setButtonLink((prev) => !prev)
  }

  return (
    <>
      <button className={styles.btnFont} data-testid="testBtn" onClick={onClick}>
        Tweet
      </button>
      {buttonLink ? (
        <Suspense fallback={<div>Loading...</div>}>
          <Route path="/popup" component={() => <PopupTweet SetPostNews={SetPostNews} />} />
        </Suspense>
      ) : null}
    </>
  )
})
