import React, { FC, useState, useCallback } from 'react'
import { Link } from 'react-router-dom'
import styles from './profileNav.module.css'
import { MediaTest } from './componentsTest/MediaTest'

const NavItems = ['Tweets', 'Tweets and replies', 'Media', 'Likes'] //вопросики

export const ProfileNav: FC = React.memo(() => {
  const [itemActive, setItemActive] = useState('Tweets')
  const [buttonLink, setuttonLink] = useState(true)

  const onClickHandler = useCallback(
    (e: React.MouseEvent) => {
      const title = e.currentTarget.innerHTML
      setItemActive(title)
      setuttonLink((prev) => !prev)
    },
    [setItemActive]
  )

  return (
    <nav className={styles.wrp}>
      <ul className={styles.profileNav}>
        {NavItems.map((item, index) => (
          <li key={index}>
            <button
              data-testid={item}
              onClick={onClickHandler}
              // to={`/${item}`}
              className={item === itemActive ? styles.profileNavActive : styles.profileItems}
            >
              {buttonLink ? item : <MeMediaTestdia />}
            </button>
          </li>
        ))}
      </ul>
    </nav>
  )
})
