import React, { FC, useState } from 'react'
import {Router, Route, Switch } from 'react-router-dom'
import {BrowserRouter} from 'react-router-dom';


import styles from './App.module.css'

import { Nav } from './components/Nav/Nav'
import {Content} from './components/Content/Content'
import { Sidebar } from './components/Sidebar/Sidebar'
import {Bookmarks} from './components/Bookmarks/Bookmarks'
// import {ProfileNav} from './components/ProfileNav/ProfileNav'
// import {Profile} from './components/Profile/Profile'
// import {ProfileContent} from './components/Content/ProfileContent'




import { createBrowserHistory } from 'history'
const browserHistory = createBrowserHistory()

type postProps = {
  value: string,
  id: number,
  isFlagLike: boolean
}

export const App: FC = () => {
  const [postNews, SetPostNews] = useState<postProps[]>([])

  const toggle = (id: number) => {
    SetPostNews(postNews.map((post) => {
        if (post.id === id) {
          post.isFlagLike = !post.isFlagLike
        }
        return post
      })
    )
  }

  console.log(postNews)


  return (
    <>
        {/* <Router history={browserHistory}></Router> */}
        <BrowserRouter>
        {/* <Switch> */}
        <main className={styles.main}>

          <Nav SetPostNews={SetPostNews} />

          <section className={styles.list}>
            {/* <BrowserRouter> */}
            <Switch>
          <Route exact component={() => <Content toggle={toggle} postNews={postNews}/>} path="/" />
          <Route  component={Bookmarks} path="/Bookmarks1" />


          </Switch>
            {/* </BrowserRouter> */}


          {/* <Route exact component={() => <Content toggle={toggle} postNews={postNews}/>} path="/" /> */}

           {/* <Route path="/Bookmarks1" component={() => <Bookmarks />} exact />
           <Route path="/Profile1" component={() => <Content toggle={toggle} postNews={postNews}/>} exact /> */}
          </section>
          <Sidebar />

        </main>
        {/* <Router/> */}
        {/* </Switch> */}
        </BrowserRouter>
    </>
  )
}
