import React, { FC, useCallback } from "react"

import styles from "./news.module.css"

import comment from "../../images/svg/post/comment.svg"
import retweet from "../../images/svg/post/retweet.svg"
import like from "../../images/svg/post/like.svg"
import share from "../../images/svg/post/share.svg"
import statistics from "../../images/svg/post/statistics.svg"

export const postNavSvg = [comment, retweet, like, share, statistics]

type props = {
  toggle: any
  id: any
  el: any
}

export const Like: FC<props> = ({ toggle, id, el }) => {
  const counterLike = 1

  const toggl = useCallback(
    (id: number) => {
      toggle((prev: any) =>
        prev.forEach((post: any) => {
          if (post.isFlagLike === id) {
            post.isFlagLike = !post.isFlagLike
          }
          // return { ...post, isFlagLike: post.id === id ? !post.isFlagLike : post.isFlagLike }
        })
      )
    },
    [toggle]
  )

  return (
    <nav>
      <ul className={styles.postNav}>
        {postNavSvg.map((item, index) => {
          if (item === "/static/media/like.c755a9fe.svg") {
            return (
              <li key={item} className={styles.liLike}>
                <button
                  type="button"
                  onClick={(e) => {
                    toggl(id)
                  }}
                >
                  <img
                    src={item}
                    alt={`${index}`}
                    className={el.isFlagLike ? styles.itemLike : ""}
                  />
                </button>
                <span>{el.isFlagLike ? counterLike : ""}</span>
              </li>
            )
          } else {
            return (
              <li key={index} className={styles.liLike}>
                <button type="button">
                  <img src={item} alt={`${index}`} />
                </button>
              </li>
            )
          }
        })}
      </ul>
    </nav>
  )
}
