import React, { useState } from 'react'

import Button from '../../components/groupsBtn/Button'
import groups from './dataGroups'
import styles from '../../sidebar.module.css'

type GroupsItemType = {
  onClickHandler?: (isOpen: boolean) => void
}

/**
 * 1. Стили компонента должны лежать рядом с компонентом;
 * 2. Найти какой-нибудь плагин, который будет подчеркивать / исправлять ошибки в словах
 * 3. В return не должно быть логики про преобразование данных. Старайся операции подобные slice и похожих или уносить из компонента (как firstThreeGroups) или используй мемоизацию;
 *
 * @constructor
 */

const firstThreeGroups = groups.slice(0, 3)

export const GroupsItem: React.FC<GroupsItemType> = ({ onClickHandler }) => {
  const [showItems, setShowItems] = useState(false)

  const clickHandler = () => {
    setShowItems(!showItems)

    if (onClickHandler) {
      onClickHandler(showItems)
    }
  }

  return (
    <>
      {(showItems ? groups : firstThreeGroups).map((item, index) => {
        return (
          <ul key={index} className={styles.groupsList}>
            <li className={styles.groupsItem}>
              <img className={styles.titleImages} src={item.images} />
              <div>
                <div className={styles.gpoupsName}>
                  <h3>
                    {item.title}
                    {item.confirmation ? (
                      <img className={styles.confirmation} src={item.confirmation} />
                    ) : (
                      ''
                    )}
                  </h3>
                  <p>{item.description}</p>
                </div>
                <p>
                  {item.promoted ? <img src={item.promotedIcon} /> : ''}
                  {item.promoted}
                </p>
              </div>
              <Button />
            </li>
          </ul>
        )
      })}
      <button type="button" className={styles.showLink} onClick={clickHandler}>
        Show more
      </button>
    </>
  )
}

export default GroupsItem
