import React, { FC, useState, useCallback } from "react"

import { BrowserRouter as Router, Switch, Route } from "react-router-dom"

import styles from "./App.module.css"

import { Nav } from "./components/Nav/Nav"
import { Content } from "./components/Content/Content"
import { Sidebar } from "./components/Sidebar/Sidebar"
import { Bookmarks } from "./components/Bookmarks/Bookmarks"
// import {ProfileNav} from './components/ProfileNav/ProfileNav'
// import {Profile} from './components/Profile/Profile'
// import {ProfileContent} from './components/Content/ProfileContent'

// import { createBrowserHistory } from "history"
// const browserHistory = createBrowserHistory()

import { createBrowserHistory } from "history"
const browserHistory = createBrowserHistory()

type postProps = {
  value: string
  id: number
  isFlagLike: boolean
}

export const App: FC = () => {
  const [postNews, SetPostNews] = useState<postProps[]>([])

  const toggle = useCallback(
    (id: number) => {
      SetPostNews(
        postNews.map((post) => {
          if (post.id === id) {
            post.isFlagLike = !post.isFlagLike
          }
          return post
        })
      )
    },
    [postNews]
  )

  console.log(postNews)

  return (
    <>
      <main className={styles.main}>
        <Router>
          <Nav SetPostNews={SetPostNews} />

          <section className={styles.list}>
            {/* <Switch> */}
            <Route
              exact
              component={() => <Content toggle={toggle} postNews={postNews} />}
              path="/"
            />
            <Route component={() => <Bookmarks postNews={postNews} />} path="/Bookmarks1" />
            {/* </Switch> */}

            {/* <Content toggle={toggle} postNews={postNews} /> */}
          </section>

          <Sidebar />
        </Router>
      </main>
    </>
  )
}
